mapper.update:
UPDATE order_detail
SET order_id = #{orderId,jdbcType=BIGINT},
  product_id = #{productId,jdbcType=BIGINT},
  count = #{count,jdbcType=INTEGER},
  ct = #{ct,jdbcType=TIMESTAMP}
WHERE id = #{id,jdbcType=BIGINT}

