mapper.update:
UPDATE user
SET mobile = #{mobile,jdbcType=VARCHAR},
  pwd = #{pwd,jdbcType=VARCHAR},
  name = #{name,jdbcType=VARCHAR},
  ct = #{ct,jdbcType=TIMESTAMP}
WHERE id = #{id,jdbcType=BIGINT}

